import { BingChat } from 'bing-chat'
import fs from 'fs';

async function example() {
  const api = new BingChat({
    cookie: process.env.BING_COOKIE
  })
  const requestVar = process.argv[2]
  const res = await api.sendMessage(requestVar)
  console.log(res.text)
fs.writeFile('file.txt', res.text, (err) => {
  if (err) {
    console.error(err);
    return;
  }
  console.log('Đã lưu nội dung vào file thành công!');
});
}
example()